// Let's try find it recursively, each recursive step is a step further away
// func findZero(startRow, startCol, i, j, step int, mat [][]int) int {
//
// }

type Cell struct {
	code string
	row  int
	col  int
	val  int
	step int
}

func newCell(i, j, step int, mat *[][]int) Cell {
	return Cell{
		code: fmt.Sprintf("%d-%d", i, j),
		row:  i,
		col:  j,
		val:  (*mat)[i][j],
		step: step,
	}
}

func findZero(i, j int, mat *[][]int) int {
	m := len(*mat)      // number of rows.
	n := len((*mat)[0]) // number of columns.
	q := []Cell{newCell(i, j, 0, mat)}
	vq := map[string]bool{
		q[0].code: true,
	}

	for len(q) > 0 {
		cell := q[0]
		q = q[1:]

		row := cell.row
		col := cell.col

		// up
		if row-1 >= 0 {
			c := newCell(row-1, col, cell.step+1, mat)
			// if this cell is 0 no need to continue
			if c.val == 0 {
				return c.step
			}
			// only add unvisited cells to the queue
			if vq[c.code] == false {
				vq[c.code] = true
				q = append(q, c)
			}
		}
		// down
		if row+1 < m {
			c := newCell(row+1, col, cell.step+1, mat)
			if c.val == 0 {
				return c.step
			}
			if vq[c.code] == false {
				vq[c.code] = true
				q = append(q, c)
			}
		}
		// left
		if col-1 >= 0 {
			c := newCell(row, col-1, cell.step+1, mat)
			if c.val == 0 {
				return c.step
			}
			if vq[c.code] == false {
				vq[c.code] = true
				q = append(q, c)
			}
		}
		// right
		if col+1 < n {
			c := newCell(row, col+1, cell.step+1, mat)
			if c.val == 0 {
				return c.step
			}
			if vq[c.code] == false {
				vq[c.code] = true
				q = append(q, c)
			}
		}
	}

	return 0
}

// updateMatrix will step through each of the cells in the array. At each cell
// it will implement a bread-first-search to find the number of steps to get to
// the first `0`.
func updateMatrix(mat [][]int) [][]int {
	m := len(mat)                      // number of rows
	n := len(mat[0])                   // number of columns
	distanceMatrix := make([][]int, m) // make the rows
	for i := 0; i < m; i++ {
		distanceMatrix[i] = make([]int, n) // make columns
	}

	for i, row := range mat {
		for j, cell := range row {
			if cell == 0 {
				distanceMatrix[i][j] = 0
				continue // we can skip this cell
			}
			distance := findZero(i, j, &mat)
			distanceMatrix[i][j] = distance
		}
	}
	return distanceMatrix
}

// Now for the reverse, we will find the first 0 and work backwards.

// From each zero, take a step and set the step distance.
